package javaday2;

public class CheckNumberIsPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 10;
		if (number > 0 ) {
			System.out.println("This Number is Positive");
		}
		else if (number < 0 ) {
			System.out.println("This Number is Negative");
		}

	}

}
